import React from "react";
import axios from "axios";
import Movie from "./Movie";

class App extends React.Component {
  state = {
    isLoading: true,
    movies: [],
  };

  getMovies = async ( ) => {
    const {
      data: {
        data: {movies}
      }
    } = await axios.get("https://yts-proxy.now.sh/list_movies.json?sort_by=rating")
    //sort_by=rating
    this.setState({movies, isLoading: false})

  }
  // 컴포넌트가 그려지면(render) 호출되는 생명주기 함수 
  componentDidMount( ) {
    //영화 데이터 로딩
    this.getMovies()
  }

  render( ) {
    const {isLoading, movies} = this.state;
    return <div>{isLoading ? "Loading..." : movies.map((movie) => {
      console.log(movie)
      return (
        <Movie
          id={movie.id}
          year={movie.year}
          title={movie.title}
          summary={movie.summary}
          poster={movie.medium_cover_image}
        />
      )
    })} </div>;
  }

  /* 
  1. render로 컴포넌트를 그림 
  2. 처음에는 state가 true 이기 때문에 Loading...이 화면에 표시됨
  3. 컴포넌트가 그려지고 나서 componentDidMount가 실행
  4. setState 6초 후에 isLoading state를 false로 바꿈
  5. 화면에 we are ready가 표시됨

  => 이 componentDidMount로 영화 데이터를 로딩한다.
  */
}

export default App;
